local b_len = #arr
local b_arr = ffi_new('uint8_t[?]', b_len)

for i=1,b_len,1 do
    b_arr[i-1] = arr[i]
end

local StrNumber = ffi.typeof('StrNumber')

local bf = bloomfilter.LoadBF(b_arr, b_len)
bf = ffi_gc(bf, bloomfilter.DestroyBF)

local ele = StrNumber {str = "4501310677070684"}
local ok, is_in = pcall(bloomfilter.MightContainStrNumber, bf, ele)
if not ok then
    ngx.say(string_for("error accur1. %s", is_in))
end
ngx.say(str_format("is_in:%s", is_in))

local ele2 = 4501310677070684
ok, is_in = pcall(bloomfilter.MightContainNumber, bf, ele2)
if not ok then
    ngx.say(str_format("error accur2. %s", is_in))
end
ngx.say(str_format("is_in:%s", is_in))

local w_bf = bloomfilter.NewBF(1200, 0.001)
w_bf = ffi_gc(w_bf, bloomfilter.DestroyBF)
local ok, is_changed = pcall(bloomfilter.PutUint64, w_bf, ele2)
if not ok then
    ngx.say(str_format("error accur3. %s", is_changed))
end
ngx.say(str_format("is_changed:%s", tostring(is_changed)))

local bitset = ffi.cast('uint8_t *', w_bf.bitset)
--write bitset to redis

local data = ffi.cast('uint64_t *', bitset + 6)
local length = w_bf.bitset.length
for i=0,length - 1,1 do
    ngx.say(str_format("data[%s]:%s", i, data[i]))
end

ok, is_in = pcall(bloomfilter.MightContainNumber, w_bf, ele2)
if not ok then
    ngx.say(str_format("error accur2. %s", is_in))
end
ngx.say(str_format("is_in:%s", is_in))
